package MallardFillmore;

use RSS::Tree;
use strict;

our @ISA = qw(RSS::Tree);


sub new {
    my $class = shift;

    my $self = $class->SUPER::new(
        'http://duckcover.blogspot.com/feeds/posts/default?alt=rss',
        'fillmore', 'Duck and Cover'
    );

    $self->cachefile('/Users/mcafee/.mf-cache', 10 * 60);

    return $self;

}

sub render {
    my ($self, $item) = @_;
    my $anchor = $item->body->findvalue(
        '//a[contains(@href,"/fun/Mallard_Fillmore/")]'
    );
    defined $anchor
        or return $self->SUPER::render($item);
    my $href = $anchor->attr('href');
    require LWP::Simple;
    defined(my $page = LWP::Simple::get($href))
        or return $self->SUPER::render($item);
    require HTML::TreeBuilder::XPath;
    my $pagetree = HTML::TreeBuilder::XPath->new_from_content($page);
    my ($image) = $pagetree->findnodes('//img[@id="feature"]');
    $anchor->replace_with($image);
    return $itemtree->as_HTML("");
}

1;
